import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/DashboardPage.css';

// Mock data for offers
const mockOffers = [
  {
    id: 1,
    merchant: 'Amazon',
    description: 'Get 10% off on your first purchase',
    expiryDate: '2024-12-31',
  },
  {
    id: 2,
    merchant: 'Walmart',
    description: 'Free shipping on orders over $50',
    expiryDate: '2024-12-15',
  },
  {
    id: 3,
    merchant: 'Target',
    description: 'Buy one get one free on selected items',
    expiryDate: '2024-12-20',
  },
];

// Mock data for claimed offers
const mockClaimedOffers = [
  {
    id: 4,
    merchant: 'Best Buy',
    description: '20% off on electronics',
    claimedDate: '2024-03-01',
    status: 'Active',
  },
  {
    id: 5,
    merchant: 'Starbucks',
    description: 'Free drink with any purchase',
    claimedDate: '2024-02-28',
    status: 'Used',
  },
];

function DashboardPage() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [activeTab, setActiveTab] = useState('available');
  const [offers] = useState(mockOffers);
  const [claimedOffers] = useState(mockClaimedOffers);

  const handleLogout = () => {
    // TODO: Add logout logic here
    navigate('/login');
  };

  const filteredOffers = offers.filter(offer =>
    offer.merchant.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>PayPerks Dashboard</h1>
        <button onClick={handleLogout} className="logout-button">
          Logout
        </button>
      </header>

      <div className="search-container">
        <input
          type="text"
          placeholder="Search offers by merchant..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input"
        />
      </div>

      <div className="tabs">
        <button
          className={`tab-button ${activeTab === 'available' ? 'active' : ''}`}
          onClick={() => setActiveTab('available')}
        >
          Available Offers
        </button>
        <button
          className={`tab-button ${activeTab === 'claimed' ? 'active' : ''}`}
          onClick={() => setActiveTab('claimed')}
        >
          Claimed Offers
        </button>
      </div>

      <div className="offers-container">
        {activeTab === 'available' ? (
          <div className="offers-list">
            {filteredOffers.length > 0 ? (
              filteredOffers.map(offer => (
                <div key={offer.id} className="offer-card">
                  <h3>{offer.merchant}</h3>
                  <p>{offer.description}</p>
                  <p className="expiry-date">Expires: {offer.expiryDate}</p>
                  <button className="claim-button">Claim Offer</button>
                </div>
              ))
            ) : (
              <p className="no-offers">No offers found</p>
            )}
          </div>
        ) : (
          <div className="offers-list">
            {claimedOffers.length > 0 ? (
              claimedOffers.map(offer => (
                <div key={offer.id} className="offer-card claimed">
                  <h3>{offer.merchant}</h3>
                  <p>{offer.description}</p>
                  <p className="claimed-date">Claimed: {offer.claimedDate}</p>
                  <p className={`status ${offer.status.toLowerCase()}`}>
                    {offer.status}
                  </p>
                </div>
              ))
            ) : (
              <p className="no-offers">No claimed offers</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default DashboardPage;
